import React from 'react';
import { Form, useField, Formik, useFormik } from 'formik'; // used Formik for its lightweight & scalability
import { FormColumn } from '../../Styles';
import * as Yup from 'yup'; // used Yup for validation

// for email input field
const formik = useFormik({
    initialValues: {
        email: ''
    }
})
const EmailInput = ({ label, ...props }) => {
    const [field, meta] = useField(props);

    // return component & display error if there's an error
    return(
      <>
      <label htmlFor={props.id || props.name}>{label}</label>
      <input className="text-input" {...field} {...props} /> 

      {meta.touched && meta.error ? (
        <div className="error">{meta.error}</div>
      ):null}
      </>
    )
  }

function SubscriptionForm () {
    return (
        <FormColumn>
            <Formik initialValues={formik.email} validationSchema={Yup.object({
            email: Yup.string()
                .email('Email format should be abc@domain.com')
                .max(50, 'Must be 50 chars or less')
                .required('This field is required')
            })}
            // POST to placeholder API when the form is submitted
            onSubmit={(values, { setSubmitting, resetForm }) => {
            resetForm();
            setSubmitting(false);
            // setTimeout(() => {
            //   alert(JSON.stringify(values, null, 1));
            //   resetForm();
            //   setSubmitting(false);
            // }, 1000);
            fetch('https://jsonplaceholder.typicode.com/posts', {
                method: 'POST',
                body: JSON.stringify({
                email: values
                }),
                headers: {
                "Content-type": "application/json; charset=UTF-8"
                }
            })
            .then(response => response.json())
            .then(json => console.log(json))
            }}
            >
            {props => (
                <Form className="newsletterForm">
                <EmailInput label="Email" name="email" type="email" placeholder="abc@email.com" onChange={formik.handleChange} data-testid="test-input"></EmailInput>
                <button type="submit">{props.isSubmitting ? 'Joining...' : 'Join the Movement'}</button>
                <p className="footnote">We won't spam you. Unsubscribe at anytime.</p>
                </Form>
            )}
            </Formik>
        </FormColumn>
    )
}

export default SubscriptionForm;
